// UXI Test Configuration Workflow
// Operations: create_test, update_test, delete_test, list_tests, run_test, schedule_test

const operation = $input.item(0).json.operation;
const testData = $input.item(0).json.test_data || {};
const uxiApiUrl = $input.item(0).json.uxi_api_url || 'https://api.uxi.aruba.com';
const testId = $input.item(0).json.test_id;
const sensorId = $input.item(0).json.sensor_id;
const testType = $input.item(0).json.test_type || 'network_performance';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: create_test, update_test, delete_test, list_tests, run_test, schedule_test');
}

if (['update_test', 'delete_test', 'run_test'].includes(operation) && !testId) {
  throw new Error('Test ID is required for update, delete, and run operations');
}

if (['create_test', 'run_test', 'schedule_test'].includes(operation) && !sensorId) {
  throw new Error('Sensor ID is required for create, run, and schedule operations');
}

// Test type configurations
const testTypeConfigs = {
  'network_performance': {
    name: 'Network Performance Test',
    description: 'Comprehensive network performance testing',
    tests: [
      {
        name: 'WiFi Connectivity',
        type: 'connectivity',
        parameters: {
          target_ssid: 'auto_detect',
          connection_timeout: 30,
          retry_attempts: 3
        },
        thresholds: {
          signal_strength: -70,
          connection_time: 10
        }
      },
      {
        name: 'Internet Speed Test',
        type: 'speed',
        parameters: {
          download_servers: ['auto'],
          upload_servers: ['auto'],
          test_duration: 10,
          parallel_connections: 4
        },
        thresholds: {
          download_speed: 25,
          upload_speed: 5,
          latency: 50,
          jitter: 20
        }
      },
      {
        name: 'DNS Resolution',
        type: 'dns',
        parameters: {
          dns_servers: ['8.8.8.8', '1.1.1.1'],
          test_domains: ['google.com', 'microsoft.com', 'aruba.com'],
          timeout: 5
        },
        thresholds: {
          resolution_time: 2,
          success_rate: 95
        }
      }
    ],
    frequency: 300, // 5 minutes
    timeout: 60
  },
  'application_performance': {
    name: 'Application Performance Test',
    description: 'Real-world application performance testing',
    tests: [
      {
        name: 'Web Browsing',
        type: 'http',
        parameters: {
          target_urls: ['https://www.google.com', 'https://www.microsoft.com'],
          browser_type: 'chrome',
          page_load_timeout: 30,
          follow_redirects: true
        },
        thresholds: {
          page_load_time: 3,
          dns_lookup_time: 1,
          tcp_connect_time: 1,
          ssl_handshake_time: 1
        }
      },
      {
        name: 'Email Performance',
        type: 'email',
        parameters: {
          email_server: 'outlook.office365.com',
          protocol: 'imap',
          port: 993,
          ssl_enabled: true
        },
        thresholds: {
          login_time: 5,
          message_download_time: 3,
          sync_time: 10
        }
      },
      {
        name: 'Video Conference',
        type: 'video',
        parameters: {
          service: 'teams',
          test_duration: 60,
          video_quality: '720p',
          audio_quality: 'high'
        },
        thresholds: {
          video_latency: 100,
          audio_latency: 50,
          packet_loss: 1,
          jitter: 20
        }
      }
    ],
    frequency: 600, // 10 minutes
    timeout: 120
  },
  'user_experience': {
    name: 'User Experience Test',
    description: 'Comprehensive user experience monitoring',
    tests: [
      {
        name: 'WiFi Roaming',
        type: 'roaming',
        parameters: {
          roaming_path: 'auto_detect',
          roaming_threshold: -75,
          test_duration: 300
        },
        thresholds: {
          roaming_time: 2,
          connection_drops: 0,
          data_continuity: 99
        }
      },
      {
        name: 'Authentication',
        type: 'auth',
        parameters: {
          auth_method: 'wpa2_enterprise',
          certificate_validation: true,
          timeout: 30
        },
        thresholds: {
          auth_time: 10,
          success_rate: 98
        }
      },
      {
        name: 'Device Onboarding',
        type: 'onboarding',
        parameters: {
          device_types: ['mobile', 'laptop', 'iot'],
          onboarding_method: 'automatic',
          test_duration: 180
        },
        thresholds: {
          onboarding_time: 60,
          success_rate: 95
        }
      }
    ],
    frequency: 900, // 15 minutes
    timeout: 300
  },
  'security_compliance': {
    name: 'Security Compliance Test',
    description: 'Network security and compliance validation',
    tests: [
      {
        name: 'Network Security',
        type: 'security',
        parameters: {
          vulnerability_scan: true,
          port_scan: true,
          ssl_validation: true,
          certificate_check: true
        },
        thresholds: {
          vulnerabilities: 0,
          open_ports: 'whitelist_only',
          ssl_grade: 'A',
          cert_expiry_days: 30
        }
      },
      {
        name: 'Compliance Check',
        type: 'compliance',
        parameters: {
          standards: ['pci_dss', 'hipaa', 'gdpr'],
          policy_validation: true,
          access_control_test: true
        },
        thresholds: {
          compliance_score: 95,
          policy_violations: 0,
          access_failures: 0
        }
      }
    ],
    frequency: 3600, // 1 hour
    timeout: 180
  },
  'iot_connectivity': {
    name: 'IoT Connectivity Test',
    description: 'IoT device connectivity and performance',
    tests: [
      {
        name: 'IoT Device Discovery',
        type: 'discovery',
        parameters: {
          discovery_protocols: ['mdns', 'upnp', 'bluetooth'],
          scan_duration: 60,
          device_categories: ['sensors', 'actuators', 'gateways']
        },
        thresholds: {
          discovery_time: 30,
          device_response_rate: 90
        }
      },
      {
        name: 'IoT Data Transmission',
        type: 'data_transmission',
        parameters: {
          protocols: ['mqtt', 'coap', 'http'],
          message_size: 1024,
          frequency: 60,
          encryption: true
        },
        thresholds: {
          transmission_time: 1,
          message_loss: 1,
          encryption_overhead: 10
        }
      }
    ],
    frequency: 1800, // 30 minutes
    timeout: 120
  },
  'voice_quality': {
    name: 'Voice Quality Test',
    description: 'VoIP and voice quality assessment',
    tests: [
      {
        name: 'VoIP Call Quality',
        type: 'voip',
        parameters: {
          codec: 'g711',
          call_duration: 60,
          test_scenarios: ['local', 'remote', 'international']
        },
        thresholds: {
          mos_score: 4.0,
          latency: 100,
          jitter: 20,
          packet_loss: 1
        }
      },
      {
        name: 'Voice Recognition',
        type: 'voice_recognition',
        parameters: {
          test_phrases: ['standard_phrases'],
          noise_levels: ['quiet', 'moderate', 'noisy'],
          languages: ['en-US']
        },
        thresholds: {
          recognition_accuracy: 95,
          response_time: 2,
          confidence_score: 85
        }
      }
    ],
    frequency: 1200, // 20 minutes
    timeout: 180
  }
};

// Generate test configuration based on test type
let testConfig = testData;
if (operation === 'create_test' && testType && testTypeConfigs[testType]) {
  const typeConfig = testTypeConfigs[testType];
  testConfig = {
    ...testData,
    name: testData.name || typeConfig.name,
    description: testData.description || typeConfig.description,
    test_type: testType,
    tests: typeConfig.tests,
    frequency: testData.frequency || typeConfig.frequency,
    timeout: testData.timeout || typeConfig.timeout,
    enabled: testData.enabled !== undefined ? testData.enabled : true
  };
}

// Add scheduling configuration
if (operation === 'schedule_test') {
  testConfig.schedule = {
    type: testData.schedule_type || 'interval',
    interval: testData.interval || 300, // 5 minutes
    start_time: testData.start_time || new Date().toISOString(),
    end_time: testData.end_time || null,
    timezone: testData.timezone || 'UTC',
    enabled: testData.schedule_enabled !== undefined ? testData.schedule_enabled : true
  };
}

// Prepare output data
const outputData = {
  operation,
  test_id: testId,
  sensor_id: sensorId,
  test_config: testConfig,
  test_type: testType,
  uxi_api_url: uxiApiUrl,
  timestamp: new Date().toISOString(),
  validation_rules: {
    test_id_required: ['update_test', 'delete_test', 'run_test'].includes(operation),
    sensor_id_required: ['create_test', 'run_test', 'schedule_test'].includes(operation),
    test_config_required: ['create_test', 'update_test', 'schedule_test'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'list_tests') {
  outputData.list_filters = {
    sensor_id: testData.sensor_id || 'all',
    test_type: testData.test_type || 'all',
    status: testData.status || 'all',
    limit: testData.limit || 100
  };
}

if (operation === 'run_test') {
  outputData.execution_options = {
    immediate: testData.immediate || true,
    timeout: testData.timeout || 60,
    retry_on_failure: testData.retry_on_failure || true,
    max_retries: testData.max_retries || 3
  };
}

return [{ json: outputData }];

// Process UXI Test API response and prepare success response
const operation = $input.item(0).json.operation;
const apiResponse = $input.item(0).json;

let successMessage = '';
let responseData = {};

switch (operation) {
  case 'create_test':
    successMessage = `Test '${apiResponse.name}' created successfully`;
    responseData = {
      test_id: apiResponse.id,
      name: apiResponse.name,
      test_type: apiResponse.test_type,
      sensor_id: apiResponse.sensor_id,
      frequency: apiResponse.frequency,
      status: 'created'
    };
    break;

  case 'update_test':
    successMessage = `Test '${apiResponse.name}' updated successfully`;
    responseData = {
      test_id: apiResponse.id,
      name: apiResponse.name,
      changes_applied: apiResponse.changes_applied,
      status: 'updated'
    };
    break;

  case 'delete_test':
    successMessage = `Test deleted successfully`;
    responseData = {
      test_id: apiResponse.id,
      status: 'deleted',
      cleanup_completed: true
    };
    break;

  case 'list_tests':
    successMessage = `Retrieved ${apiResponse.tests.length} tests`;
    responseData = {
      total_tests: apiResponse.tests.length,
      tests: apiResponse.tests,
      filters_applied: apiResponse.filters
    };
    break;

  case 'run_test':
    successMessage = `Test execution started successfully`;
    responseData = {
      test_id: apiResponse.test_id,
      execution_id: apiResponse.execution_id,
      status: 'running',
      estimated_duration: apiResponse.estimated_duration,
      started_at: apiResponse.started_at
    };
    break;

  case 'schedule_test':
    successMessage = `Test scheduled successfully`;
    responseData = {
      test_id: apiResponse.test_id,
      schedule_id: apiResponse.schedule_id,
      schedule: apiResponse.schedule,
      status: 'scheduled',
      next_execution: apiResponse.next_execution
    };
    break;

  default:
    successMessage = 'Test operation completed successfully';
    responseData = apiResponse;
}

return [{
  json: {
    success: true,
    operation: operation,
    message: successMessage,
    data: responseData,
    timestamp: new Date().toISOString()
  }
}];

// Error handling for UXI test operations
const operation = $input.item(0).json.operation || 'unknown';
const error = $input.item(0).json.error || {};
const testId = $input.item(0).json.test_id;
const sensorId = $input.item(0).json.sensor_id;

// Categorize error types
let errorCategory = 'unknown';
let errorMessage = error.message || 'Unknown error occurred';
let shouldRetry = false;
let rollbackRequired = false;

if (error.code) {
  switch (error.code) {
    case 'AUTHENTICATION_ERROR':
    case 401:
      errorCategory = 'authentication';
      errorMessage = 'UXI API authentication failed. Please check credentials.';
      shouldRetry = false;
      break;

    case 'AUTHORIZATION_ERROR':
    case 403:
      errorCategory = 'authorization';
      errorMessage = 'Insufficient permissions for UXI test operations.';
      shouldRetry = false;
      break;

    case 'TEST_NOT_FOUND':
    case 'SENSOR_NOT_FOUND':
    case 404:
      errorCategory = 'not_found';
      errorMessage = `Test or sensor not found. Test ID: ${testId}, Sensor ID: ${sensorId}`;
      shouldRetry = false;
      break;

    case 'VALIDATION_ERROR':
    case 400:
      errorCategory = 'validation';
      errorMessage = 'Invalid test configuration parameters.';
      shouldRetry = false;
      break;

    case 'TEST_EXECUTION_ERROR':
      errorCategory = 'execution';
      errorMessage = 'Test execution failed. Check sensor connectivity and configuration.';
      shouldRetry = true;
      break;

    case 'SENSOR_OFFLINE':
      errorCategory = 'sensor_offline';
      errorMessage = 'Sensor is offline or unreachable.';
      shouldRetry = true;
      break;

    case 'NETWORK_ERROR':
    case 'TIMEOUT':
      errorCategory = 'network';
      errorMessage = 'Network connectivity issue with UXI API.';
      shouldRetry = true;
      break;

    case 'RATE_LIMIT_EXCEEDED':
    case 429:
      errorCategory = 'rate_limit';
      errorMessage = 'UXI API rate limit exceeded. Please retry later.';
      shouldRetry = true;
      break;

    case 'TEST_CONFLICT':
    case 409:
      errorCategory = 'conflict';
      errorMessage = 'Test name conflicts with existing test or sensor is busy.';
      shouldRetry = false;
      break;

    case 'INTERNAL_ERROR':
    case 500:
      errorCategory = 'server';
      errorMessage = 'UXI API internal server error.';
      shouldRetry = true;
      break;

    default:
      errorCategory = 'unknown';
      shouldRetry = false;
  }
}

// Determine if rollback is required
if (['create_test', 'update_test', 'schedule_test'].includes(operation)) {
  rollbackRequired = true;
}

// Generate error response
const errorResponse = {
  success: false,
  operation: operation,
  error: {
    category: errorCategory,
    message: errorMessage,
    code: error.code,
    details: error.details || {}
  },
  test_id: testId,
  sensor_id: sensorId,
  should_retry: shouldRetry,
  rollback_required: rollbackRequired,
  timestamp: new Date().toISOString(),
  troubleshooting: {
    authentication: 'Verify UXI API credentials and token validity',
    authorization: 'Check UXI API permissions and user roles',
    not_found: 'Verify test ID and sensor ID exist in UXI dashboard',
    validation: 'Check test configuration parameters against API schema',
    execution: 'Verify sensor connectivity and test configuration',
    sensor_offline: 'Check sensor network connectivity and power status',
    network: 'Verify network connectivity to UXI API endpoint',
    rate_limit: 'Implement exponential backoff and retry logic',
    conflict: 'Use unique test names and ensure sensor is not busy',
    server: 'Contact UXI support if issue persists'
  }
};

return [{ json: errorResponse }];

