// Standard Aruba Monitoring Input Validation Template
const input = items[0].json;

// Validation functions
const validateDeviceFilter = (filter) => {
  const validFilters = ['all', 'switches', 'aps', 'gateways', 'controllers'];
  const cleanFilter = String(filter || 'all').toLowerCase();
  if (!validFilters.includes(cleanFilter)) {
    throw new Error(`Invalid device_filter: ${filter}. Must be one of: ${validFilters.join(', ')}`);
  }
  return cleanFilter;
};

const validateDeviceSerial = (serial) => {
  if (!serial) return null;
  const serialPattern = /^[A-Za-z0-9._-]{3,50}$/;
  if (!serialPattern.test(serial)) {
    throw new Error('Invalid device_serial format');
  }
  return String(serial).substring(0, 50);
};

const validateThreshold = (value, name, min, max, defaultValue) => {
  if (value === undefined || value === null) return defaultValue;
  const numValue = parseFloat(value);
  if (isNaN(numValue) || numValue < min || numValue > max) {
    throw new Error(`Invalid ${name}: must be number between ${min} and ${max}`);
  }
  return numValue;
};

// Create standardized monitoring request
const monitoringRequest = {
  // Request metadata
  timestamp: new Date().toISOString(),
  request_id: `mon-${Date.now()}-${Math.random().toString(36).substr(2, 8)}`,
  request_type: input.device_serial ? 'targeted' : 'bulk',
  workflow_template: 'aruba-monitoring-v1.0.0',

  // Device targeting
  device_filter: validateDeviceFilter(input.device_filter),
  device_serial: validateDeviceSerial(input.device_serial),
  location_filter: input.location_filter ? String(input.location_filter).substring(0, 100) : null,

  // Monitoring thresholds
  thresholds: {
    cpu_critical: validateThreshold(input.cpu_critical, 'cpu_critical', 50, 100, 90),
    cpu_warning: validateThreshold(input.cpu_warning, 'cpu_warning', 30, 95, 80),
    memory_critical: validateThreshold(input.memory_critical, 'memory_critical', 50, 100, 95),
    memory_warning: validateThreshold(input.memory_warning, 'memory_warning', 30, 95, 85),
    temperature_critical: validateThreshold(input.temperature_critical, 'temperature_critical', 30, 100, 65),
    temperature_warning: validateThreshold(input.temperature_warning, 'temperature_warning', 20, 90, 55),
    uptime_minimum: validateThreshold(input.uptime_minimum, 'uptime_minimum', 60, 86400, 300)
  },

  // Feature configuration
  features: {
    include_interfaces: input.include_interfaces !== false,
    include_wireless: input.include_wireless !== false,
    include_power: input.include_power !== false,
    include_environment: input.include_environment !== false,
    detailed_analysis: input.detailed_analysis === true
  },

  // Output configuration
  output: {
    format: input.output_format || 'json',
    include_raw_data: input.include_raw_data === true,
    notification_channels: input.notification_channels || ['slack'],
    severity_filter: input.severity_filter || 'all'
  }
};

return [{ json: monitoringRequest }];

// Standard Aruba Monitoring Data Processor
const apiResponse = items[0].json;
const request = $('Validate & Prepare Request').first().json;
const thresholds = request.thresholds;
const features = request.features;

// Handle API errors
if (apiResponse.error || apiResponse.status >= 400) {
  throw new Error(`API Error: ${apiResponse.error || apiResponse.message || 'Unknown API error'}`);
}

// Extract device data
const devices = apiResponse.result?.devices || apiResponse.devices || apiResponse.data || [];

if (!Array.isArray(devices) || devices.length === 0) {
  return [{
    json: {
      request_id: request.request_id,
      timestamp: new Date().toISOString(),
      status: 'no_devices_found',
      total_devices: 0,
      healthy_devices: 0,
      warning_devices: 0,
      critical_devices: 0,
      total_alerts: 0,
      devices: [],
      summary: 'No devices found matching the criteria'
    }
  }];
}

// Process each device
const processedDevices = devices.map(device => {
  // Sanitize device data
  const deviceId = String(device.serial || device.device_serial || device.id || 'unknown').substring(0, 50);
  const deviceName = String(device.name || device.hostname || deviceId).substring(0, 100);
  const deviceType = String(device.type || device.device_type || 'unknown').substring(0, 50);

  // Extract and validate metrics
  const metrics = {
    cpu_usage: Math.min(Math.max(parseFloat(device.cpu_usage) || 0, 0), 100),
    memory_usage: Math.min(Math.max(parseFloat(device.memory_usage) || 0, 0), 100),
    temperature: Math.min(Math.max(parseFloat(device.temperature) || 25, -50), 150),
    uptime: Math.max(parseInt(device.uptime) || 0, 0),
    disk_usage: Math.min(Math.max(parseFloat(device.disk_usage) || 0, 0), 100)
  };

  // Additional metrics based on features
  if (features.include_wireless && device.wireless) {
    metrics.wireless = {
      client_count: parseInt(device.wireless.client_count) || 0,
      channel_utilization: parseFloat(device.wireless.channel_utilization) || 0,
      signal_strength: parseFloat(device.wireless.signal_strength) || 0
    };
  }

  if (features.include_interfaces && device.interfaces) {
    metrics.interfaces = {
      total_ports: parseInt(device.interfaces.total) || 0,
      active_ports: parseInt(device.interfaces.active) || 0,
      utilization: parseFloat(device.interfaces.utilization) || 0
    };
  }

  if (features.include_power && device.power) {
    metrics.power = {
      consumption: parseFloat(device.power.consumption) || 0,
      supply_voltage: parseFloat(device.power.voltage) || 0,
      poe_usage: parseFloat(device.power.poe_usage) || 0
    };
  }

  // Health analysis
  const alerts = [];
  let overallStatus = 'healthy';

  // CPU analysis
  if (metrics.cpu_usage >= thresholds.cpu_critical) {
    alerts.push({
      type: 'cpu_critical',
      severity: 'critical',
      message: `CPU usage critical: ${metrics.cpu_usage.toFixed(1)}%`,
      value: metrics.cpu_usage,
      threshold: thresholds.cpu_critical,
      timestamp: new Date().toISOString()
    });
    overallStatus = 'critical';
  } else if (metrics.cpu_usage >= thresholds.cpu_warning) {
    alerts.push({
      type: 'cpu_warning',
      severity: 'warning',
      message: `CPU usage warning: ${metrics.cpu_usage.toFixed(1)}%`,
      value: metrics.cpu_usage,
      threshold: thresholds.cpu_warning,
      timestamp: new Date().toISOString()
    });
    if (overallStatus === 'healthy') overallStatus = 'warning';
  }

  // Memory analysis
  if (metrics.memory_usage >= thresholds.memory_critical) {
    alerts.push({
      type: 'memory_critical',
      severity: 'critical',
      message: `Memory usage critical: ${metrics.memory_usage.toFixed(1)}%`,
      value: metrics.memory_usage,
      threshold: thresholds.memory_critical,
      timestamp: new Date().toISOString()
    });
    overallStatus = 'critical';
  } else if (metrics.memory_usage >= thresholds.memory_warning) {
    alerts.push({
      type: 'memory_warning',
      severity: 'warning',
      message: `Memory usage warning: ${metrics.memory_usage.toFixed(1)}%`,
      value: metrics.memory_usage,
      threshold: thresholds.memory_warning,
      timestamp: new Date().toISOString()
    });
    if (overallStatus === 'healthy') overallStatus = 'warning';
  }

  // Temperature analysis
  if (metrics.temperature >= thresholds.temperature_critical) {
    alerts.push({
      type: 'temperature_critical',
      severity: 'critical',
      message: `Temperature critical: ${metrics.temperature.toFixed(1)}¬∞C`,
      value: metrics.temperature,
      threshold: thresholds.temperature_critical,
      timestamp: new Date().toISOString()
    });
    overallStatus = 'critical';
  } else if (metrics.temperature >= thresholds.temperature_warning) {
    alerts.push({
      type: 'temperature_warning',
      severity: 'warning',
      message: `Temperature warning: ${metrics.temperature.toFixed(1)}¬∞C`,
      value: metrics.temperature,
      threshold: thresholds.temperature_warning,
      timestamp: new Date().toISOString()
    });
    if (overallStatus === 'healthy') overallStatus = 'warning';
  }

  // Connectivity analysis
  const isOnline = device.status === 'online' || device.status === 'up' || device.connectivity?.status === 'online';
  if (!isOnline) {
    alerts.push({
      type: 'connectivity',
      severity: 'critical',
      message: 'Device offline or unreachable',
      last_seen: device.last_seen || device.connectivity?.last_seen,
      timestamp: new Date().toISOString()
    });
    overallStatus = 'critical';
  }

  return {
    device_id: deviceId,
    device_name: deviceName,
    device_type: deviceType,
    location: device.location || 'unknown',
    ip_address: device.ip_address || device.management_ip || 'unknown',
    firmware_version: device.firmware_version || 'unknown',
    model: device.model || 'unknown',

    status: {
      overall: overallStatus,
      connectivity: isOnline ? 'online' : 'offline',
      last_seen: device.last_seen || new Date().toISOString()
    },

    metrics: metrics,
    alerts: alerts,

    timestamp: new Date().toISOString()
  };
});

// Generate summary
const summary = {
  request_id: request.request_id,
  workflow_template: request.workflow_template,
  timestamp: new Date().toISOString(),

  // Device counts
  total_devices: processedDevices.length,
  healthy_devices: processedDevices.filter(d => d.status.overall === 'healthy').length,
  warning_devices: processedDevices.filter(d => d.status.overall === 'warning').length,
  critical_devices: processedDevices.filter(d => d.status.overall === 'critical').length,
  offline_devices: processedDevices.filter(d => d.status.connectivity === 'offline').length,

  // Alert summary
  total_alerts: processedDevices.reduce((sum, d) => sum + d.alerts.length, 0),
  critical_alerts: processedDevices.reduce((sum, d) => sum + d.alerts.filter(a => a.severity === 'critical').length, 0),
  warning_alerts: processedDevices.reduce((sum, d) => sum + d.alerts.filter(a => a.severity === 'warning').length, 0),

  // Request context
  request_context: {
    device_filter: request.device_filter,
    device_serial: request.device_serial,
    location_filter: request.location_filter,
    features_enabled: Object.keys(request.features).filter(key => request.features[key])
  },

  // Processed devices
  devices: processedDevices,

  status: 'completed'
};

return [{ json: summary }];

