// Format standardized response
const data = items[0].json;

const response = {
  status: 'success',
  timestamp: new Date().toISOString(),
  workflow: {
    template: data.workflow_template || 'aruba-monitoring-v1.0.0',
    version: '1.0.0'
  },
  summary: {
    request_id: data.request_id,
    total_devices: data.total_devices,
    healthy_devices: data.healthy_devices,
    warning_devices: data.warning_devices,
    critical_devices: data.critical_devices,
    offline_devices: data.offline_devices,
    total_alerts: data.total_alerts,
    critical_alerts: data.critical_alerts,
    warning_alerts: data.warning_alerts
  },
  execution: {
    completed_at: new Date().toISOString(),
    processing_time: Date.now() - new Date(data.timestamp).getTime(),
    status: data.status
  }
};

// Include device details if requested
if (data.request_context?.features_enabled?.includes('detailed_analysis')) {
  response.devices = data.devices;
}

return [{ json: response }];

// Standardized template error handling
const error = $input.first().error;
const context = $input.first().json || {};

const templateError = {
  error_id: `tmpl-err-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`,
  timestamp: new Date().toISOString(),
  template: 'aruba-monitoring-v1.0.0',
  error_type: error.name || 'TemplateError',
  message: 'Monitoring workflow error',
  request_id: context.request_id || 'unknown',
  step: $runIndex || 'unknown',
  severity: 'high',
  status: 'error'
};

// Secure error logging
console.error('TEMPLATE_ERROR:', {
  error_id: templateError.error_id,
  template: templateError.template,
  type: error.name,
  message: error.message?.substring(0, 200),
  context: {
    request_id: context.request_id,
    workflow: $workflow.name,
    step: $runIndex
  }
});

return [{ json: templateError }];

