// Standard Security Event Validation Template
const eventData = items[0].json;

// Required field validation
const requiredFields = {
  event_id: { type: 'string', pattern: /^[A-Za-z0-9_-]{6,50}$/, maxLength: 50 },
  threat_type: { type: 'string', enum: ['malware', 'intrusion', 'dos', 'vulnerability', 'policy_violation', 'anomaly', 'phishing', 'ransomware', 'other'] },
  severity: { type: 'string', enum: ['low', 'medium', 'high', 'critical'] },
  device_id: { type: 'string', pattern: /^[A-Za-z0-9._-]{3,50}$/, maxLength: 50 },
  timestamp: { type: 'string', format: 'iso_date' }
};

// Validation functions
const validateField = (value, fieldName, rules) => {
  if (!value && rules.required !== false) {
    throw new Error(`Missing required field: ${fieldName}`);
  }

  if (value && typeof value !== rules.type) {
    throw new Error(`Invalid ${fieldName} type: expected ${rules.type}`);
  }

  if (rules.pattern && !rules.pattern.test(value)) {
    throw new Error(`Invalid ${fieldName} format`);
  }

  if (rules.enum && !rules.enum.includes(String(value).toLowerCase())) {
    throw new Error(`Invalid ${fieldName}: must be one of ${rules.enum.join(', ')}`);
  }

  if (rules.maxLength && String(value).length > rules.maxLength) {
    return String(value).substring(0, rules.maxLength);
  }

  return value;
};

const validateIpAddress = (ip) => {
  if (!ip) return null;
  const ipPattern = /^(?:(?:25[0-5]|2[0-4]\d|[01]?\d\d?)\.){3}(?:25[0-5]|2[0-4]\d|[01]?\d\d?)$/;
  if (!ipPattern.test(ip)) {
    throw new Error('Invalid IP address format');
  }
  return ip;
};

const validateConfidenceScore = (score) => {
  if (score === undefined || score === null) return 0.8; // Default
  const numScore = parseFloat(score);
  if (isNaN(numScore) || numScore < 0 || numScore > 1) {
    throw new Error('confidence_score must be between 0 and 1');
  }
  return numScore;
};

// Validate all required fields
const validatedEvent = {};
for (const [fieldName, rules] of Object.entries(requiredFields)) {
  validatedEvent[fieldName] = validateField(eventData[fieldName], fieldName, rules);
}

// Normalize and validate optional fields
validatedEvent.threat_type = String(validatedEvent.threat_type).toLowerCase();
validatedEvent.severity = String(validatedEvent.severity).toLowerCase();
validatedEvent.timestamp = new Date(validatedEvent.timestamp).toISOString();

// Validate and sanitize optional fields
validatedEvent.source_ip = validateIpAddress(eventData.source_ip);
validatedEvent.destination_ip = validateIpAddress(eventData.destination_ip);
validatedEvent.user_id = eventData.user_id ? String(eventData.user_id).substring(0, 100) : null;
validatedEvent.description = eventData.description ? String(eventData.description).substring(0, 1000) : '';
validatedEvent.confidence_score = validateConfidenceScore(eventData.confidence_score);
validatedEvent.external_reference = eventData.external_reference ? String(eventData.external_reference).substring(0, 200) : null;

// Add system metadata
validatedEvent.received_at = new Date().toISOString();
validatedEvent.processing_id = `sec-${Date.now()}-${Math.random().toString(36).substr(2, 8)}`;
validatedEvent.workflow_template = 'aruba-security-v1.0.0';

// Calculate threat score using standard algorithm
const severityScores = {
  'low': 25,
  'medium': 50,
  'high': 75,
  'critical': 95
};

const threatTypeMultipliers = {
  'malware': 1.2,
  'ransomware': 1.3,
  'intrusion': 1.1,
  'dos': 1.0,
  'vulnerability': 0.9,
  'policy_violation': 0.8,
  'anomaly': 0.9,
  'phishing': 1.1,
  'other': 1.0
};

const baseThreatScore = severityScores[validatedEvent.severity];
const typeMultiplier = threatTypeMultipliers[validatedEvent.threat_type] || 1.0;
const threatScore = Math.min(Math.round(baseThreatScore * typeMultiplier * validatedEvent.confidence_score), 100);

// Determine response actions
const responseMatrix = {
  isolation_required: threatScore >= 90,
  quarantine_required: threatScore >= 80,
  enhanced_monitoring: threatScore >= 70,
  alert_soc: threatScore >= 60,
  log_only: threatScore < 60,
  escalation_required: threatScore >= 95,
  auto_response_enabled: threatScore >= 85
};

// Determine notification level
let notificationLevel = 'info';
if (threatScore >= 90) notificationLevel = 'critical';
else if (threatScore >= 75) notificationLevel = 'high';
else if (threatScore >= 50) notificationLevel = 'medium';
else notificationLevel = 'low';

// Create enriched security event
const enrichedEvent = {
  ...validatedEvent,

  // Threat assessment
  threat_score: threatScore,
  threat_category: threatScore >= 80 ? 'high_risk' : threatScore >= 60 ? 'medium_risk' : 'low_risk',

  // Response configuration
  response_actions: responseMatrix,
  notification_level: notificationLevel,

  // Processing metadata
  processing_status: 'validated',
  validation_timestamp: new Date().toISOString()
};

return [{ json: enrichedEvent }];

// Critical Threat Response Handler
const event = items[0].json;

// Log critical security event
console.log('CRITICAL_SECURITY_EVENT:', {
  event_id: event.event_id,
  processing_id: event.processing_id,
  device_id: event.device_id,
  threat_type: event.threat_type,
  threat_score: event.threat_score,
  severity: event.severity,
  source_ip: event.source_ip,
  timestamp: event.timestamp
});

// Prepare immediate isolation request
const isolationPayload = {
  action: 'isolate_device',
  device_id: event.device_id,
  reason: `Critical security threat: ${event.threat_type} (Score: ${event.threat_score})`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  processing_id: event.processing_id,
  isolation_type: 'immediate',
  rollback_timer: 3600, // 1 hour auto-rollback
  approval_required: false, // Auto-approve for critical threats
  timestamp: new Date().toISOString()
};

// Prepare SOC escalation
const socEscalation = {
  priority: 'P1',
  event_id: event.event_id,
  processing_id: event.processing_id,
  threat_category: event.threat_category,
  threat_score: event.threat_score,
  affected_device: event.device_id,
  threat_type: event.threat_type,
  automated_actions: ['device_isolation', 'enhanced_monitoring'],
  requires_immediate_attention: true,
  escalation_timestamp: new Date().toISOString()
};

// Create response
const criticalResponse = {
  ...event,
  response_type: 'critical_automated',
  isolation_payload: isolationPayload,
  soc_escalation: socEscalation,
  actions_taken: ['device_isolation_initiated', 'soc_escalation_created'],
  status: 'processing_critical_response',
  processing_timestamp: new Date().toISOString()
};

return [{ json: criticalResponse }];

// High Threat Response Handler
const event = items[0].json;

// Log high security event
console.log('HIGH_SECURITY_EVENT:', {
  event_id: event.event_id,
  processing_id: event.processing_id,
  device_id: event.device_id,
  threat_type: event.threat_type,
  threat_score: event.threat_score
});

// Prepare enhanced monitoring request
const monitoringPayload = {
  action: 'enhance_monitoring',
  device_id: event.device_id,
  reason: `High security threat detected: ${event.threat_type} (Score: ${event.threat_score})`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  processing_id: event.processing_id,
  monitoring_level: 'enhanced',
  monitoring_duration: 3600, // 1 hour enhanced monitoring
  include_network_traffic: true,
  include_process_monitoring: true,
  alert_threshold: 'low', // Lower threshold for alerts
  timestamp: new Date().toISOString()
};

// Prepare quarantine assessment
const quarantineAssessment = {
  recommended: event.response_actions.quarantine_required,
  device_id: event.device_id,
  threat_score: event.threat_score,
  manual_approval_required: true,
  assessment_criteria: {
    threat_type: event.threat_type,
    confidence_score: event.confidence_score,
    device_criticality: 'unknown' // To be assessed
  },
  timestamp: new Date().toISOString()
};

const highResponse = {
  ...event,
  response_type: 'high_monitored',
  monitoring_payload: monitoringPayload,
  quarantine_assessment: quarantineAssessment,
  actions_taken: ['enhanced_monitoring_initiated', 'quarantine_assessment_created'],
  status: 'processing_high_response',
  processing_timestamp: new Date().toISOString()
};

return [{ json: highResponse }];

// Medium Threat Response Handler
const event = items[0].json;

// Log medium security event
console.log('MEDIUM_SECURITY_EVENT:', {
  event_id: event.event_id,
  processing_id: event.processing_id,
  device_id: event.device_id,
  threat_type: event.threat_type,
  threat_score: event.threat_score
});

// Prepare analysis and logging request
const analysisPayload = {
  action: 'analyze_and_log',
  device_id: event.device_id,
  reason: `Medium security threat detected: ${event.threat_type} (Score: ${event.threat_score})`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  processing_id: event.processing_id,
  analysis_priority: 'normal',
  log_retention: '30_days',
  correlation_enabled: true,
  baseline_comparison: true,
  timestamp: new Date().toISOString()
};

// Prepare trend analysis
const trendAnalysis = {
  device_id: event.device_id,
  threat_type: event.threat_type,
  look_back_period: '7_days',
  correlation_fields: ['source_ip', 'user_id', 'threat_type'],
  alert_on_pattern: true,
  timestamp: new Date().toISOString()
};

const mediumResponse = {
  ...event,
  response_type: 'medium_analyzed',
  analysis_payload: analysisPayload,
  trend_analysis: trendAnalysis,
  actions_taken: ['threat_analysis_initiated', 'trend_analysis_queued'],
  status: 'processing_medium_response',
  processing_timestamp: new Date().toISOString()
};

return [{ json: mediumResponse }];

