// Comprehensive Security Audit Logging
const event = items[0].json;

// Create detailed audit entry
const auditEntry = {
  // Audit metadata
  audit_id: `audit-${Date.now()}-${Math.random().toString(36).substr(2, 8)}`,
  audit_timestamp: new Date().toISOString(),
  workflow_template: event.workflow_template || 'aruba-security-v1.0.0',

  // Event identification
  event_id: event.event_id,
  processing_id: event.processing_id,
  external_reference: event.external_reference,

  // Threat details
  threat_assessment: {
    type: event.threat_type,
    severity: event.severity,
    score: event.threat_score,
    category: event.threat_category,
    confidence: event.confidence_score,
    description: event.description
  },

  // Affected assets
  affected_assets: {
    primary_device: event.device_id,
    source_ip: event.source_ip,
    destination_ip: event.destination_ip,
    user_id: event.user_id
  },

  // Response details
  response_summary: {
    type: event.response_type,
    actions_taken: event.actions_taken || [],
    isolation_executed: event.isolation_payload ? true : false,
    monitoring_enhanced: event.monitoring_payload ? true : false,
    analysis_queued: event.analysis_payload ? true : false,
    soc_escalated: event.soc_escalation ? true : false
  },

  // Compliance and governance
  compliance: {
    processed_within_sla: true, // Immediate processing
    automated_response: true,
    human_intervention_required: event.response_actions?.escalation_required || false,
    approval_bypass_authorized: event.threat_score >= 90,
    data_retention_period: '7_years' // Security events
  },

  // Processing performance
  processing_metrics: {
    validation_time: new Date(event.validation_timestamp).getTime() - new Date(event.received_at).getTime(),
    total_processing_time: Date.now() - new Date(event.received_at).getTime(),
    workflow_version: event.workflow_template
  }
};

// Create SIEM-compatible log entry
const siemLog = {
  timestamp: new Date().toISOString(),
  event_type: 'security_workflow_completed',
  severity: event.severity,
  source: 'n8n_aruba_security_workflow',
  event_id: event.event_id,
  device_id: event.device_id,
  threat_score: event.threat_score,
  response_type: event.response_type,
  actions_taken: event.actions_taken,
  processing_id: event.processing_id
};

// Output for audit trail
console.log('SECURITY_AUDIT:', JSON.stringify(auditEntry, null, 2));
console.log('SIEM_LOG:', JSON.stringify(siemLog));

const response = {
  ...event,
  audit_entry: auditEntry,
  siem_log: siemLog,
  status: 'audit_completed',
  audit_timestamp: new Date().toISOString()
};

return [{ json: response }];

// Format standardized security response
const data = items[0].json;

const securityResponse = {
  status: 'success',
  timestamp: new Date().toISOString(),
  workflow: {
    template: data.workflow_template || 'aruba-security-v1.0.0',
    version: '1.0.0'
  },

  event_summary: {
    event_id: data.event_id,
    processing_id: data.processing_id,
    threat_type: data.threat_type,
    severity: data.severity,
    threat_score: data.threat_score,
    threat_category: data.threat_category,
    device_id: data.device_id
  },

  response_summary: {
    response_type: data.response_type,
    actions_taken: data.actions_taken || [],
    processing_time: Date.now() - new Date(data.received_at).getTime(),
    automated_actions: {
      isolation: data.isolation_payload ? 'executed' : 'not_required',
      monitoring: data.monitoring_payload ? 'enhanced' : 'standard',
      analysis: data.analysis_payload ? 'queued' : 'not_required',
      soc_escalation: data.soc_escalation ? 'escalated' : 'not_required'
    }
  },

  compliance: {
    audit_logged: data.audit_entry ? true : false,
    siem_integration: data.siem_log ? true : false,
    processing_status: data.status
  }
};

return [{ json: securityResponse }];

// Security-focused Error Handler
const error = $input.first().error;
const context = $input.first().json || {};

// Create security error response
const securityError = {
  error_id: `sec-err-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`,
  timestamp: new Date().toISOString(),
  template: 'aruba-security-v1.0.0',
  error_type: error.name || 'SecurityProcessingError',
  error_severity: 'high', // Security errors are always high severity
  message: 'Security event processing failed',

  // Context preservation
  event_id: context.event_id || 'unknown',
  processing_id: context.processing_id || 'unknown',
  device_id: context.device_id || 'unknown',
  threat_score: context.threat_score || 0,

  // Processing metadata
  failed_step: $runIndex || 'unknown',
  workflow_name: $workflow.name || 'unknown',

  status: 'error'
};

// Security incident logging
console.error('SECURITY_PROCESSING_ERROR:', {
  error_id: securityError.error_id,
  event_id: context.event_id,
  processing_id: context.processing_id,
  error_type: error.name,
  error_message: error.message?.substring(0, 200),
  stack_trace: error.stack?.substring(0, 500),
  context: {
    device_id: context.device_id,
    threat_score: context.threat_score,
    step: $runIndex,
    workflow: $workflow.name
  }
});

return [{ json: securityError }];

